# https://leetcode.com/problems/longest-substring-of-all-vowels-in-order/


"""
Example 1:
Input: word = "aeiaaioaaaaeiiiiouuuooaauuaeiu"
Output: 13
Explanation: The longest beautiful substring in word is "aaaaeiiiiouuu" of length 13.

Example 2:
Input: word = "aeeeiiiioooauuuaeiou"
Output: 5
Explanation: The longest beautiful substring in word is "aeiou" of length 5.

Example 3:
Input: word = "a"
Output: 0
Explanation: There is no beautiful substring, so return 0.
"""


# word = "aeiaaioaaaaeiiiiouuuooaauuaeiu"
# word = "aeeeiiiioooauuuaeioua" # 5
# word = "a"
# Expected: 2169
word = ""


class Solution:
    def longestBeautifulSubstring(self, word: str) -> int:
        expected, prev = set("a"), ""
        cnt, res = 0, 0
        for i in range(len(word)):
            if word[i] == "a" and word[i] in expected:
                expected = {"a","e"}
                prev = word[i]
                cnt += 1
            elif word[i] == "e" and word[i] in expected:
                expected = {"e","i"}
                prev = word[i]
                cnt += 1
            elif word[i] == "i" and word[i] in expected:
                expected = {"i","o"}
                prev = word[i]
                cnt += 1
            elif word[i] == "o" and word[i] in expected:
                expected = {"o","u"}
                prev = word[i]
                cnt += 1
            elif word[i] == "u" and word[i] in expected:
                expected = {"u"}
                prev = word[i]
                cnt += 1
            else:
                expected = {"a"}
                if word[i] == "a":
                    cnt = 1
                else:
                    cnt = 0
            if prev == "u":
                res = max(res, cnt)
        return res



solution = Solution()
print(solution.longestBeautifulSubstring(word))

# Runtime: 1164 ms
# Memory Usage: 20.1 MB

